const translations = {
    "default":{
        "allow":"Click Allow to receive notifications.",
        "unblock":"Please unblock notifications in your browser settings to receive notifications from this website.",
    },
    "en":{
        "allow":"Click Allow to receive notifications.",
        "unblock":"Please unblock notifications in your browser settings to receive notifications from this website.",
    },
    "ru":{
        "allow": "Нажмите разрешить, чтобы получать уведомления.",
        "unblock":"Пожалуйста, разблокируйте уведомления в настройках вашего браузер, чтобы получать уведомления с этого сайта.",
    },
    "es":{
        "allow": "Haga clic en Permitir para recibir notificaciones.",
        "unblock":"Por favor, desbloquee las notificaciones en la configuración de su navegador para recibir notificaciones de este sitio.",
    },
    "fr": {
        "allow": "Cliquez Autoriser pour recevoir des notifications.",
        "unblock":"Débloquez les notifications dans les paramètres de votre navigateur pour recevoir des notifications de ce site.",
    }
};

document.addEventListener("DOMContentLoaded", () => {
    const currentLang = navigator.languages.find(l => translations[l]);
    const translate = translations[currentLang] || currentLang["default"];

    console.log(currentLang);
    console.log(translate);

    const allowElement = document.querySelector("div[permission='default']>p");
    const unblockElement = document.querySelector("div[permission='denied']>p");

    allowElement.innerText = translate.allow;
    unblockElement.innerText = translate.unblock;

    console.log('azaza');
});


